var Query = require('./lib/database/Query.js');
var HttpCommunication = require('./lib/server/communication/HTTPCommunication.js');
var Update = require('./lib/database/update.js');

exports.asQuery = function (query, options) {
    return new Query(query, options);
}

exports.asUpdate = function (table, options) {
    return new Update(table, options);
}

exports.writeLog = function (logid, log) {
    var options = {};
    options[HttpCommunication.Services.Log.LOGID] = logid;
    options[HttpCommunication.Services.Log.LOG] = log;
    HttpCommunication.call(HttpCommunication.Services.LOG, options, function (err, res) {
        if (err) {
            console.log("Problem while inseting logs>>>>" + err.stack);
        }
    });
}

exports.renderFile = function (options, callback) {
    HttpCommunication.call(HttpCommunication.Services.RENDER_FILE, options, callback);
}
exports.UserService = require('./lib/user/UserService.js');
